	toggleTrigger("roomContent",false);
  toggleTrigger("infoquestContent",false);
} else {
    var util = window.dbIdentUtil;
    let desclong = match[1];
  	//se presente alla fine (condizioni) lo tolgo
    let m = desclong.match(/([^(]+)(?=\()/);
	  if (m != null) {desclong = m[1]};
    //se pezzi multipli con [] a inizio e fine le tolgo
	  m = desclong.match(/^(?:\[..\] |)(.+?)(?:\[\d+\]|)$/);
    if (m != null) {desclong = m[1]}; 
    desclong = desclong.trim()
    if (util.debug()) {console.log("Item trovato nel trigger roomContent:" + desclong)}
    if (desclong == "") return; //Mikko 231119 Bugfix per gli oggetti che hanno desclong vuota
    let r = util.withlong(desclong);
    if (r.length>0) {
      //const start = performance.now(); //PROFILAZIONE      
      let firstword = $1;
      const spaceIndex = firstword.indexOf(" ");
      if (spaceIndex>-1) {
      	firstword = firstword.substring(0, spaceIndex);
      }
      let testoident = ((r.length==1) && (r[0][util.db.fieldsPos.Provenienza]=="") ) ?
          color("[?]","maroon") : color("[?]","green");

      sub(firstword, link(testoident,()=>{
        let out = util.getStringIdents(r);
        print(out)
      },"vedi identify") + " " + firstword);
      //const duration = performance.now() - start; //PROFILAZIONE
      //print("Profilazione: " + duration, "Social"); 
    }  
}

