if (!this.healtype && !this.canPlay) return;
if (!this.pauseAutocleric && this.TSLag == "-") {
  const grp = JSON.parse(this.Gruppo);
  let mincura = this.aclMinimum || 70;
  var minhp = 100;
  var minpg = "";
  var minMov = 100;
  var minMovPg = "";
  for (let nome in grp) {
    if (nome == "tank" || nome == "mob") continue;
    // non in room o non curabile
    if (grp[nome].hide || !grp[nome].inroom || grp[nome].pkill) {
      continue;
    }
    if (grp[nome].hp < mincura && grp[nome].hp<minhp) {
      minhp = grp[nome].hp;
      minpg = nome;
    }
    if (grp[nome].mv < 25 && grp[nome].mv<minMov) {
      minMov = grp[nome].mv;
      minMovPg = nome;
    }
  }

  if (minpg) {
    this.pgdacurare = minpg;
    print("Curo: " + minpg + " (" + minhp + "% < " + mincura + "%)");
    send("heal " + minpg);
    this.pauseAutocleric = true;
    delay("reset_acl", 2000, ()=>{
      this.pauseAutocleric = false;
      const lag = this.TSLag;
      if (lag == "-") {
      	this.TSLag = "";
      	delay("reset_acl2", 100, ()=>{ this.TSLag = lag; });
      }
    });
  }
  if (minMovPg) {
    print("Refresh: " + minMovPg + " (" + minMov + "% < " + 25 + "%)");
    send("refresh " + minMovPg);
    this.pauseAutocleric = true;
    delay("reset_acl", 2000, ()=>{
      this.pauseAutocleric = false;
      const lag = this.TSLag;
      if (lag == "-") {
      	this.TSLag = "";
      	delay("reset_acl2", 100, ()=>{ this.TSLag = lag; });
      }
    });
  }
}

