if (this.disableCheckAssist == true || this.preventAutoassist == true) 
{
  //this.preventAutoassist = false
  if (this.autobash) {
    delay("checkAssist", (this.autocleric ? 2000 : 0 + 2000), () => {send("checkAssist")})
  }
  return;
}
this.disableCheckAssist = true;
setTimeout(()=>{this.disableCheckAssist=false}, 200);

const newTank = this.TSTank;
const tankState = this.TSTankCond;

let cleanCastingTimeout = () => {
  if (this.castTimeout) clearTimeout(this.castTimeout);
  this.castTimeout = 0;
}
if (this.autostop && tankState && tankState != "*" && !this.preventAutoStop && !this.isTank()) {
  this.preventAutoStop = true;
  this.preventAutoassist = true;
  if (this.TSPosizione!="In piedi") {
    send("stand");
  }
  send("~stop");
  delay("autostop1", 4000, () => {
    this.preventAutoassist = false
    send("checkAssist")
  })
  delay("autostop2", 8250, () => {
    this.preventAutoStop = false
    send("checkAssist")
  })
}
let rescuato = false;
if (this.autorescue && newTank != "*" && newTank!=this.TSPersonaggio && !this.preventAutoRescue) {
  this.preventAutoRescue = true;
  if (this.TSPosizione!="In piedi") {
    send("stand");
  }
  send("rescue " + newTank);
  rescuato = true;
  setTimeout(() => {this.preventAutoRescue = false}, 4000)
}
if (newTank != "*" && tankState == "*") {
  if ((classEnabled("autoassist")||(this.autostab))) {
    this.preventAutoassist = true
    setTimeout(() => {
      this.TSTankCond=""
      this.preventAutoassist = false
    }, 3000)
    if (this.autostab) {
      if (this.usiPierce != this.canPlay) {
        send(`${this.canPlay?"slam":"backstab"} ${this.TSMob.split(" ")[0]}`)
        if (!this.canPlay && this.afk && !this.autobash) {
          send("~stop")
        }
        return;
      }
    }
    if (!classEnabled("autoassist")) {
    	return;
    }
    if (this.autofury) {
      send(`fury ${this.TSMob.split(" ")[0]}`)
    } else {
      if (!rescuato && this.cleanupName(newTank).toLowerCase() != this.TSPersonaggio.toLowerCase()) send("assist " + this.cleanupName(newTank));
    }
    if ((this.canBash||this.canKick) && this.autobash) {
      setTimeout(()=>{if (this.inCombat()) send("bash")},200);
    }
    if (newTank==this.TSPersonaggio) return;
  }
  if (this.autocast && this.TSMob!="*" && this.spellOffensivo) {
    cleanCastingTimeout();
    const comando = this.mustMemorize ? "recall" : "cast";
    let casta = () => {
      if (this.TSMob=="*" || !this.autocast) {
        cleanCastingTimeout();
        return;
      }
      if (this.TSLag=="-") {
      	send(`${comando} '${this.spellOffensivo}' ${this.TSMob.split(" ")[0]}`)
      }
      let lag = Math.floor(this.roundtime*1.5);
      if (this.autocleric) lag += 3000;
      this.castTimeout = setTimeout(() => {casta()}, lag)
    }
    this.castTimeout = setTimeout(() => {casta()}, 0)
  }
} else if (newTank != "*" && tankState != "*") {
  if (this.autobash && this.TSLag != "+" && this.inCombat()) {
    delay("checkAssist", (this.autocleric ? 2000 : 0 + 2000),() => {send("checkAssist")})
    if (this.TSPosizione != "In piedi" && (!this.canBash && this.canKick)) {
      send("springleap");
      return;
    }
    else if (this.TSPosizione != "In piedi" && (this.canBash)) {
      send("stand");
    }
    send("bash");    
  } else if (this.autobash && this.inCombat()) {
    delay("checkAssist", (this.autocleric ? 2000 : 0 + 2000),() => {send("checkAssist")})
  }
} else if (newTank == "*") {
  cleanCastingTimeout();
}

