this.digCut = function(command, key) {
  if (!this.ultimaArma) {
    if (!this.armaPrimaria) {
      print("Definire l'arma primaria nel setup");
      return;
    }
    send(this.armaPrimaria);
  }
  this.ultimoHold = this.ultimoHold || this.holdItem || "-no-";
  const cambiaArma = (this.ultimaArma!=this.armaDigCut)
  if (cambiaArma) {
    if (this.ultimaArma!="-no-") send(`rem ${this.ultimaArma}`)
    if (this.isMonk() && this.ultimoHold) send(`rem ${this.ultimoHold}`)
    send(`gb ${this.armaDigCut}`)
    send(`~wear ${this.armaDigCut}`)
  }
  send(`~${command} ${key}`)
  if (cambiaArma) {
    send(`rem ${this.armaDigCut}`)
    if (this.ultimaArma!="-no-") send(`~wear ${this.ultimaArma}`)
    if (this.isMonk() && this.ultimoHold) send(`wear ${this.ultimoHold}`)
    send(`pb ${this.armaDigCut}`)
  }
}
