this.colorizeHp = function(num, str) {
  if (num > 95) {
    return color(str, "lightgray");
  } else if (num > 90) {
    return color(str, "green");
  } else if (num > 70) {
    return color(str, "lightgreen");
  } else if (num > 40) {
    return color(str, "yellow", null, true, false, false);
  } else if (num > 20) {
    return color(str, "orange", null, true, true, false);
  } else {
    return color(str, "red", null, true, true, true);
  }
}

this.colorizeMana = function(num, str) {
  if (num > 90) {
