  } else if (num > 70) {
    return color(str, "blue");
  } else if (num > 40) {
    return color(str, "lightblue", null, true, false, false);
  } else if (num > 20) {
    return color(str, "lightblue", null, true, true, false);
  } else {
    return color(str, "lightblue", null, true, true, true);
  }
}

this.colorizeMov = function(num, str) {
  if (num > 90) {
    return color(str, "lightgray");
  } else if (num > 70) {
    return color(str, "gray");
  } else if (num > 40) {
    return color(str, "gray", null, false, false, false);
  } else if (num > 20) {
    return color(str, "lightgray", null, true, false, false);
  } else {
    return color(str, "lightgray", null, true, false, true);
  }
}
