  case "graffiato":
	return 95;
  case "tagliato":
	return 85;
  case "ferito":
	return 65;
  case "sanguinante":
	return 40;
  case "squarciato":
	return 25;
  case "devastato":
	return 10;
  case "morente":
	return 1;
	}
  return 100;
}
