function addMinutes(date, minutes) {
    return new Date(date.getTime() + minutes*60000);
}
let sumLista = (lista) => {
  let ret = {
    "ph":0,
    "p5m":0,
    "p15m":0,
    "p1h":0
  }
  const _1h = addMinutes(new Date(), -60);
  const _15m = addMinutes(new Date(), -15);
  const _5m = addMinutes(new Date(), -5);
  lista.forEach((value,key) => {
    value = Number(value)
    if (key > _1h) {
      ret.p1h += value
    }
    if (key > _15m) {
      ret.p15m += value
    }
    if (key > _5m) {
      ret.p5m += value
    }
  });
  ret.ph = (0.42*ret.p5m*12 + 0.33*ret.p15m*4 + 0.25*ret.p1h);
  return ret;
}

if (this.listaDivini && this.listaDivini.has) {
  const ret = sumLista(this.listaDivini)
  this._stat_pqh = Math.ceil(ret.ph);
  this._stat_pq5m = ret.p5m;
  this._stat_pq15m = ret.p15m;
  this._stat_pq1h = ret.p1h;
}

if (this.listaXP && this.listaXP.has) {
  const ret = sumLista(this.listaXP)
  this._stat_xph = Math.ceil(ret.ph);
  this._stat_xp5m = ret.p5m;
  this._stat_xp15m = ret.p15m;
  this._stat_xp1h = ret.p1h;
}

