    memmato=false;
    for (let s of Object.keys(daMemmare)) {
      if (daMemmare[s]>0) {
        memmato = true;
        send(`~memo '${s}'`)
        if (!this.memorizing[s]) this.memorizing[s]=0;
        this.memorizing[s]++;
        daMemmare[s]--;
        cnt--;
        if (cnt <= 0) break;
      }
    }
  }
}

