var debug = () => this.debug;

//Carica da indexedDB to memory (util.db) la lista ident pubblica e privata
util.init_load = async function() {
  util.db = {};
  util.db.lastID = -1;
  util.db.dbinfo = {};
  util.db.ogglistPubl = [];
  util.db.ogglistPriv = [];
  util.db.fields = [];
  util.db.fieldsPos = {};
  util.debug = debug;
  util.autoprovenienzaclear();

  return new Promise(function(resolve, reject) {

    function buildFieldsPos() {
      for ( let fieldidx in util.db.fields) {
        util.db.fieldsPos[util.db.fields[fieldidx].nome] = fieldidx;
      } 
    }


    const dbconn = indexedDB.open("IdentDb", 1);
    dbconn.onerror = (event) => {
      const err = "Errore nella connessione al DB degli ident"
      reject(err)
      print(err);
    };

    dbconn.onupgradeneeded = (event) => {
      const dbresult = event.target.result;

      // Create an objectStore for this database
      const objectStore = dbresult.createObjectStore("JSONstore", { keyPath: "ID" });
      objectStore.createIndex("JSONstring", "JSONstring", { unique: true });
    }; 

    dbconn.onsuccess = (event) => {
      const dbresult = event.target.result;
      const transaction = dbresult.transaction("JSONstore", "readonly");
      const store = transaction.objectStore("JSONstore");
      //nel record 1 di JSONStore c' il db pubblico
      //nel record 2 di JSONStore c' il db privato
      const jsonqueryPubl = store.get(1);
      const jsonqueryPriv = store.get(2);

      jsonqueryPubl.onsuccess = function(event) {
      if (util.debug()) {print("Ident DB Pubblico caricato");}
        const r = event.target.result;
        if (r != undefined) {
          const objsaved = JSON.parse(r.JSONstring);
          util.db.fields = objsaved.fields;
          buildFieldsPos();
          util.db.ogglistPubl = objsaved.data;
          util.db.dbinfo = objsaved.dbinfo;
        }
      };

      jsonqueryPubl.onerror = (event) => {
        if (util.debug()) {
          print("jsonqueryPubl error. Vedi console");
          console.log(event.target.errorCode);
        }
        reject("jsonqueryPubl error. Vedi console")
      };

      jsonqueryPriv.onsuccess = function(event) {
      	if (util.debug()) {print("Ident DB Privato caricato");}
        const r = event.target.result;
        if (r != undefined) {
          const objsaved = JSON.parse(r.JSONstring);
          if (!util.db.fields || !util.db.fields.length) {
            util.db.fields = objsaved.fields;
            buildFieldsPos();
          }
          util.db.ogglistPriv = objsaved.data || [];
          if (!util.db.dbinfo) {
            util.db.dbinfo = objsaved.dbinfo;
          }
        }
      };

      jsonqueryPriv.onerror = (event) => {
        if (util.debug()) {
          print("jsonqueryPriv error. Vedi console");
          console.log(event.target.errorCode);
        }
        reject("jsonqueryPriv error. Vedi console")
      };    

      transaction.oncomplete = function() {    
        if (util.debug()) {
          print("Transaction complete");
          print("LOG Ident Publico DB Size:" + util.getSize()[0]);
          print("LOG Ident Privato DB Size:" + util.getSize()[1]);
        }
        print("Database degli ident caricato. Versione: " + util.db.dbinfo.ver + " (" + util.db.dbinfo.data + ") - " + util.db.dbinfo.msg);
        print("Elementi caricati nel db pubblico: " + util.getSize()[0]);
        print("Elementi caricati nel db privato:  " + util.getSize()[1]);


        if (!util.db.fields || !util.db.fields.length) {
          util.db.fields = [{"nome":"Oggetto","type":"TEXT"},{"nome":"Tipo","type":"TEXT"},{"nome":"DescrizioneShort","type":"TEXT"},{"nome":"PosizioneIndossabile","type":"TEXT"},{"nome":"Proprieta","type":"TEXT"},{"nome":"Peso","type":"NUMBER"},{"nome":"Valore","type":"NUMBER"},{"nome":"Rent","type":"NUMBER"},{"nome":"DescrizioneLong","type":"TEXT"},{"nome":"Identify","type":"TEXT"},{"nome":"Provenienza","type":"TEXT"},{"nome":"Rare","type":"BOOL"},{"nome":"NoRent","type":"BOOL"},{"nome":"DannoArma","type":"NUMBER"},{"nome":"Danno","type":"NUMBER"},{"nome":"SP","type":"NUMBER"},{"nome":"HP","type":"NUMBER"},{"nome":"Mana","type":"NUMBER"},{"nome":"Affect1Tipo","type":"TEXT"},{"nome":"Affect1Valore","type":"TEXT"},{"nome":"Affect2Tipo","type":"TEXT"},{"nome":"Affect2Valore","type":"TEXT"},{"nome":"Affect3Tipo","type":"TEXT"},{"nome":"Affect3Valore","type":"TEXT"},{"nome":"Affect4Tipo","type":"TEXT"},{"nome":"Affect4Valore","type":"TEXT"},{"nome":"Affect5Tipo","type":"TEXT"},{"nome":"Affect5Valore","type":"TEXT"},{"nome":"ID","type":"TEXT/NUMBER"},{"nome":"Nota","type":"TEXT"}];
          buildFieldsPos();
          if (util.debug()) {
            print("Caricamento preset Fields");
          }
        }



        dbresult.close();
        resolve()
      };  
    };
  });
}


//Salve da memoria a indexedDB i 2 database.
util.save = async function(savePublic=false) {
  return new Promise(function(resolve, reject) {
    const dbconn = indexedDB.open("IdentDb", 1);
    dbconn.onerror = (event) => {
      print("Errore nella connessione al DB degli ident");
      reject("Errore nella connessione al DB degli ident")
    };

    dbconn.onsuccess = (event) => {
      const dbresult = event.target.result;
      const transaction = dbresult.transaction("JSONstore", "readwrite");
      const store = transaction.objectStore("JSONstore");
      if (savePublic) {
        const saveobj = {dbinfo: util.db.dbinfo, fields:util.db.fields, data:util.db.ogglistPubl}
        const addqueryPubl = store.put({ID:1, JSONstring: JSON.stringify(saveobj)});

        addqueryPubl.onsuccess = function(event) {
          if (util.debug()) {print("Ident DB Pubblico salvato");}
        };

        addqueryPubl.onerror = (event) => {
          if (util.debug()) {
            print("addqueryPubl error. Vedi console");
            console.log(event.target.errorCode);
          }
          reject("addqueryPubl error. Vedi console")
        };   
      } else {
        const saveobjPriv = {dbinfo: {}, fields:util.db.fields, data:util.db.ogglistPriv}
        const addqueryPriv = store.put({ID:2, JSONstring: JSON.stringify(saveobjPriv)});

        addqueryPriv.onsuccess = function(event) {
          if (util.debug()) {print("Ident DB Privato salvato");}
        };

        addqueryPriv.onerror = (event) => {
          if (util.debug()) {
            print("addqueryPriv error. Vedi console");
            console.log(event.target.errorCode);
          }
          reject("addqueryPriv error. Vedi console")
        };
      }    

      transaction.oncomplete = function() {
        if (util.debug()) {
          print("Transaction complete");
          print("LOG Ident Publico DB Size:" + util.getSize()[0]);
          print("LOG Ident Privato DB Size:" + util.getSize()[1]);
        }
        dbresult.close();
        resolve()
      };  
    };
  });
}


//Cerca l'oggetto basando su key, descshort e desclong
//restituisce l'oggetto se trovato oppure null
//Si usa l'assunto che gli oggetti del db sono univoci per Oggetto+Short+Long
//Qualora ce ne fossero pi d'uno viene restituito il primo
util.find = function(ogg) {
  for (let objdb of this.db.ogglistPriv.concat(this.db.ogglistPubl)) {
    if (
        (objdb[util.db.fieldsPos.Oggetto] === ogg.Oggetto) &&
        (objdb[util.db.fieldsPos.DescrizioneShort] === ogg.DescrizioneShort) &&
        (objdb[util.db.fieldsPos.DescrizioneLong] === ogg.DescrizioneLong)
    )
    {return objdb;}
  }
  return null;
}


//restituisce l'elenco degli oggetti con la descshort specificata
util.withshort = function(descshort) {
    let found = [];
    for (let objdb of this.db.ogglistPriv.concat(this.db.ogglistPubl)) {
        if ((objdb[util.db.fieldsPos.DescrizioneShort] == descshort) && !this.isalreadyin(found, objdb))  {
            found.push(objdb)
        }
    }
    return found;
}

//restituisce l'elenco degli oggetti con la desclong specificata
util.withlong = function(desclong) {
    let found = [];
    for (let objdb of this.db.ogglistPriv.concat(this.db.ogglistPubl)) {
        if ((objdb[util.db.fieldsPos.DescrizioneLong].split("\n")[0] == desclong) && !this.isalreadyin(found, objdb))  {
            found.push(objdb)
        }
    }
    return found;
}


//Cerca l'oggetto basando su ID
//restituisce l'oggetto se trovato oppure null
//Si usa l'assunto che gli oggetti del db sono univoci per ID
//Qualora ce ne fossero pi d'uno viene restituito il primo
util.findID = function(id) {	
    if (this.debug()) {
		print("Ricerca dell'oggetto con ID: -->" + id + "<--");
	}
	for (let objdb of this.db.ogglistPriv.concat(this.db.ogglistPubl)) {
      if (String(objdb[util.db.fieldsPos.ID]) === String(id)) {return objdb;}
  	}
  	return null;
}


//La funzione delID permette di rimuovere un elemento dal db
// inernamente limitata a cancellare solo da db Privato.
util.delID = function(id) {
  this.db.ogglistPriv = this.db.ogglistPriv.filter( _obj => (String(_obj[util.db.fieldsPos.ID]) != String(id)) ); 
  this.save();
}


//restituisce l'elenco degli oggetti che contengono
//tutti gli elementi specificati nel testo e divisi da |, nell'ident
util.search = function(text) {
    let criteriricerca = text.split("|");
    let found = [];
    for (let objdb of this.db.ogglistPriv.concat(this.db.ogglistPubl)) {
      let criteriotrovato = true; 
      for (let criterio of criteriricerca) {
      	  if (
            (objdb[util.db.fieldsPos.Identify] + "\n" + objdb[util.db.fieldsPos.Provenienza]).toLowerCase().search(criterio.toLowerCase())<0
          ) {
          criteriotrovato = false;
          break;
          }
        } 
      if ( criteriotrovato && !this.isalreadyin(found, objdb) ) {
      	//trovato un oggetto che soddisfa i requisiti
        //e non gi tra quelli trovati       
        found.push(objdb);
      }    
    }
    return found;
}

//Data una lista, verifica se l'oggetto  gi presente nella lista
//Da usare nelle varie funzioni find e search per non mettere duplicati
util.isalreadyin = function(list, ogg) {
  let isgiatrovato = false;
  for (let oggettolista of list) {
    if (
      (oggettolista[util.db.fieldsPos.Oggetto] === ogg[util.db.fieldsPos.Oggetto]) && 
      (oggettolista[util.db.fieldsPos.DescrizioneShort] === ogg[util.db.fieldsPos.DescrizioneShort]) &&
      (oggettolista[util.db.fieldsPos.DescrizioneLong] === ogg[util.db.fieldsPos.DescrizioneLong])  
    ) {
      return true;
    }
  }
  return false;
}

