function importFromFile(callback) {
    if (!callback) return;
    let inp = document.createElement("input");
    inp.type = "file";
    inp.style.visibility = "hidden";

    inp.addEventListener("change", (e) => {
        let file = e.target.files[0];
        if (!file) {
            return;
        }

        let reader = new FileReader();
        reader.onload = (e1) => {
            let text = e1.target.result;
            callback(text)
        };
        reader.readAsText(file);

    });

    document.body.appendChild(inp);
    inp.click();
    document.body.removeChild(inp);
}

async function loadOnlineDB(prefix) {
  //print(prefix + "/identDbPublicVersion.json?rng="+Math.random())
  const res1 = await fetch(prefix + "/identDbPublicVersion.json?rng="+Math.random())
  const verData = await res1.json();

  if (verData.dbinfo.ver > (util.db.dbinfo?.ver || 0)) {
    // versione nuova online, aggiorniamo il pubblico

  	const res2 = await fetch(prefix + "/identDbPublic.json?rng="+Math.random())
  	const dbObj = await res2.json();
    importPublicoObj(dbObj)
	  print("Eseguo aggiornamento del DB Identify pubblico a v" + verData.dbinfo.ver)

  } else {
    //print("La versione del DB Identify pubblico e' aggiornata all'ultima versione disponibile sul server.")
  }
}

