function downloadJsonToFile(json, filename) {
    let jsonstr = JSON.stringify(json, null, 2);
    downloadString(jsonstr, filename);
}

function downloadString(jsonstr, filename) {
    let blob = new Blob([jsonstr], { type: "octet/stream" });
    let url = window.URL.createObjectURL(blob);

    let link = document.createElement("a");
    link.setAttribute("href", url);
    link.setAttribute("download", filename);
    link.style.visibility = "hidden";

    document.body.appendChild(link);
    link.click();
    window.URL.revokeObjectURL(url);
    document.body.removeChild(link);
}

function formattedDate(d = new Date) {
    return [d.getDate(), d.getMonth()+1, d.getFullYear()].map(n => n < 10 ? `0${n}` : `${n}`).join('/');
  }

if (database == "pubblico") {
  const saveobj = {dbinfo: util.db.dbinfo, fields:util.db.fields, data: util.db.ogglistPubl}
  downloadJsonToFile(saveobj,"identdbPubblico.json");
} else if (database == "privato") {
  const privdbinfo = {
    ver: util.db.dbinfo.ver,
    data: formattedDate(),
    msg: "Exported from WebClient" 
  }
  const saveobj = {dbinfo: privdbinfo, fields:util.db.fields, data: util.db.ogglistPriv}
  downloadJsonToFile(saveobj,"identdbPrivato.json");
}

