if (!match[1]) {
  //no parametro => rotazione armi
  if (this.rotazioneArmi) rotazioneArmi();
  return;
}
//Passato paramento quindi monto l'arma prevista

const isUltimaArmaStandard = () => (
  (this.ultimaArma==this.armaSlash) || 
  (this.ultimaArma==this.armaBlunt) || 
  (this.ultimaArma==this.armaPierce) || 
  (this.ultimaArma==this.armaExtra) || 
  (this.ultimaArma==this.arco))

const armaVoluta = match[1];

//Se l'arma voluta non  definita oppure  gi l'arma in uso esci.
//Nel caso dell'arco carica la freccia
if (!armaVoluta || this.ultimaArma == armaVoluta) {
  if (this.freccia && this.ultimaArma == this.arco) {
    send(`load ${this.freccia}`);
  }
  return;
}


//FASE 1: Prendo l'arma dalla borsa
send(`get ${armaVoluta} ${this.borsaArmi||this.borsa}`);
if (armaVoluta == this.arco) send(`get faretra ${this.borsaArmi||this.borsa}`);

//FASE 2: libero lo spazio in eq
if (this.ultimaArma!="-no-") {
  //Nelle configurazioni con Arma la tolgo.
  if (this.ultimaArma) {
    send(`rem ${this.ultimaArma}`);
  } else {
    //Se ultimaArma non  definita tolgo tutte le armi standard
    for (let a of [this.armaSlash, this.armaBlunt, this.armaPierce, this.armaExtra, this.arco]) {
      if (a && a!=armaVoluta) send(`rem ${a}`);
    }
  }
}
let newhold = this.ultimoHold;
if (this.ultimoHold!="-no-" && this.isMonk()) {
  //Nella configurazione con Hold tolgo la hold
  if (this.ultimoHold) {
    send(`rem ${this.ultimoHold}`);
  } else {
    //Se ultimoHold non  definito tolgo l'hold standard
    if (this.holdItem) send(`rem ${this.holdItem}`);
  }
  newhold="-no-";
} 

//FASE 3: Impugno l'arma
send(`~wear ${armaVoluta}`);

if (this.freccia && this.ultimaArma == this.arco) {
  send(`get ${this.freccia} ${this.borsaArmi||this.borsa}`)
  send(`load ${this.freccia}`)
}


//FASE 4: Metto l'oggetto tolto in borsa
if (this.ultimaArma!="-no") {
  //Nelle configurazioni con Arma che ho tolto la metto in borsa
  if (this.ultimaArma) {
    //se ultimaArma  definita ed  tra quelle standard la mette in borsa altrimenti
    //la lascia in inventario
    if (isUltimaArmaStandard()) send(`put ${this.ultimaArma} ${this.borsaArmi||this.borsa}`);
    } else {
      for (let a of [this.armaSlash, this.armaBlunt, this.armaPierce, this.armaExtra, this.arco]) {
        if (a && a!=armaVoluta) send(`put ${a} ${this.borsaArmi||this.borsa}`);
      }       
    }
  if (this.usiArco) {
    send(`put faretra ${this.borsaArmi||this.borsa}`);
    if (this.freccia) send(`put ${this.freccia} ${this.borsaArmi||this.borsa}`)
  }
}
if (this.ultimoHold!="-no-" && this.isMonk()) {
  //Nella configurazione con Hold metto la hold in borsa
  if (this.ultimoHold) {
    //Se oggetto standard lo metto in borsa altrimenti lo lascio in inventario
    if (this.ultimoHold==this.holdItem) send(`put ${this.ultimoHold} ${this.borsaArmi||this.borsa}`);
  } else {
    if (this.holdItem) send(`put ${this.holdItem} ${this.borsaArmi||this.borsa}`);
  }
} 

