let askStaffa = async (staffa) => {
  let nome = staffa[0]
  let key = staffa[1].key
  let bacchetta = staffa[1].tipo == "bacchetta" ? true : false
  let stash = staffa[1].prendi == "unstash" ? true : false
  let res = await Messagebox.ShowMultiInput((staffa[1].tipo + " " + staffa[0]).toUpperCase(), ["Nome","Key","Bacchetta?", "Da stash"], [nome, key, bacchetta, stash]) 
  if (res.button == 1) {
    nome = res.results[0]
    key = res.results[1]
    bacchetta = res.results[2]
    stash = res.results[3]
    staffa[0] = nome
    staffa[1].key = key
    staffa[1].tipo = bacchetta ? "bacchetta" :"bastone"
    staffa[1].prendi = stash ? "unstash" : "gb"
    staffa[1].riponi = stash ? "stash" : "pb"
    print(`${bacchetta ? "Bacchetta":"Bastone"} ${nome} con key: ${key} ${stash ? "Da stash":"Da borsa"}`)
    return staffa
  } else {
    if (res.result) {
      return null
    } else {
      return -1
    }
  }
}

let printStaffe = () => {
  print("Le tue staffe:")
  print(listastaffe.map(v => {
    return color(v[0],"white") + " : " + v[1].tipo + " con key: '" + v[1].key + "' " + (v[1].prendi == "unstash" ? "(da stash)" : "")
  }).join("\n"))
}
let configuraStaffe = async () => {
  if (listastaffe.length) {
    await Messagebox.Show("Info", "Stai per modificare la lista delle staffe.\nPremi Annulla per interrompere.\nCancella il nome ad una per cancellarla.")
  }
  for (let i = 0; i < listastaffe.length; i++) {
    let lstItem = listastaffe[i]
    let res = await askStaffa(clone(lstItem))
    if (res == null) {
      print("Ok, Interrompo.")
      return;
    }
    if (res == -1 || !res[0]) {
      print("Cancello la staffa.")
      listastaffe.splice(i, 1)
      i--;
    } else {
      listastaffe[i] = clone(res)
    }
  }
  let newEl = null
  let template = () => {
    return ["nuovo bastone",{"key":"","tipo":"bastone","prendi":"gb","riponi":"pb"}]
  }
  if ((await Messagebox.Question("Vuoi inserire nuove staffe?\nPremi Annulla quando hai finito.")).button==1) while ((newEl = await askStaffa(clone(template()))) && newEl != null && newEl != -1) {
    if (newEl[0] && newEl[1].key) {
      listastaffe.push(newEl)
    } else if (!newEl[0] || !newEl[1].key) {
      print("Errore: I campi Nome e Key sono necessari. Premi Annulla se vuoi interrompere.")
    } else {
      break;
    }
  }
  printStaffe()
  setvar("listastaffe", [...listastaffe])
  delvar("staffa")
  delvar("staffe")
  setvar("staffa", listastaffe.length ? [...listastaffe][0][0] : "")
  setvar("staffe", [...listastaffe].map(kv => kv[1].tipo + " " + kv[0]))
  print("Ok ora puoi usare alias bacchetta, bastone e usastaffa")
}
if (listastaffe.length) {
  printStaffe()
  print(link("MODIFICA", configuraStaffe))
} else {
  print("Non hai staffe configurate.")
  print(link("CONFIGURA", configuraStaffe))
}

