//Simula la creazione di un oggetto fino al livello goal. Restituisce l'oggetto e tutti i costi per generarlo con le logiche specificate nella pag web
function creaoggetto(goal) {
  let oggetto = {
    'iterazioni': 0,
    'liv': 0,
    'costo': 0,
    'numcristalli': 0,
    'numedit': 0,
    'numoveredit':0
  }

  for (let i=0; i<LIMITEPASSAGGI; i++) {
    oggetto.iterazioni += 1;
    const livoggetto = oggetto['liv'];

    let tipo;
    if (livoggetto <= 4) {
      tipo = document.querySelector(".socket-calculator #sk" + (livoggetto+1).toString()).value;
    } else {
      tipo = "insert";
    }

    if (tipo == 'insert') {
      const r = Math.random();
      if ( r<=costoinsert[livoggetto]['percok'] ) {
        // Insert ok
        oggetto.liv = costoinsert[livoggetto]['nextlivok'];
      } else {
        // Insert fallito
        oggetto.liv = costoinsert[livoggetto]['nextlivko'];
      }
      oggetto.costo += costoinsert[livoggetto]['costo'];
      oggetto.numcristalli += 1;
    } else if (tipo == 'edit') {
      oggetto.liv += 1;
      oggetto.numedit += 1;
    } else if (tipo == 'overedit') {
      oggetto.liv += 1;
      oggetto.numoveredit += 1
    }

    if (oggetto.liv == goal) {
      break
    }
  }
  return(oggetto)
}


// Restituisce l'elenco di num SIMULAZIONI oggetti creati
function calcolagoal(goal) {
  let elenco = [];
  for (let estrazione = 1; estrazione<=SIMULAZIONI; estrazione++ ) {
    const oggetto = creaoggetto(goal)
    elenco.push(oggetto)
  }

  return elenco;
}


// Data una lista di valori numerici restituisce il numero corrispondente al livello di confidenza impostato
function getconfidenza(lista) {
  function compareNumbers(a, b) {
    return a - b;
  }
  lista.sort(compareNumbers);
  return lista.slice(0, Math.floor(SIMULAZIONI*CONFIDENZA)).at(-1);
}


function calcola() {
  // Pulisce eventuale tabella preesistente
  const t = document.querySelector('.socket-calculator table.tabellarisultato')
  if (t) {t.remove();}

  const msg = document.createElement('p');
  msg.classList.add("messaggio")
  msg.appendChild(document.createTextNode("Calcolo in corso, attendere..."))
  const domRisultato = document.querySelector('.socket-calculator #risultato');
  domRisultato.appendChild(msg);

  let calcolo = async () => {
  CONFIDENZA = document.querySelector('.socket-calculator #confidenza').value;
  SIMULAZIONI = document.querySelector('.socket-calculator #simulazioni').value;
  MAXLIVELLO = document.querySelector('.socket-calculator #maxlivello').value;
  COSTOEDIT = document.querySelector('.socket-calculator #costoedit').value;

  const tbl = document.createElement('table');
  tbl.classList.add("tabellarisultato")

  let tr,td
  tr = tbl.insertRow();
  tr.classList.add("titlecells");
  td = tr.insertCell(); td.appendChild(document.createTextNode("Livello"));
  td = tr.insertCell(); td.appendChild(document.createTextNode("Coin"));
  td = tr.insertCell(); td.appendChild(document.createTextNode("Cristalli"));
  td = tr.insertCell(); td.appendChild(document.createTextNode("Divini"));  
  td = tr.insertCell(); td.appendChild(document.createTextNode("Edit"));
  td = tr.insertCell(); td.appendChild(document.createTextNode("Overedit"));


  for (let livello = 1; livello <= MAXLIVELLO; livello++) {
    const elenco = calcolagoal(livello);
    const conf_costo = getconfidenza(elenco.map(el => el.costo))
    const conf_cristalli = getconfidenza(elenco.map(el => el.numcristalli))
    const conf_edit = getconfidenza(elenco.map(el => el.numedit))
    const conf_overedit = getconfidenza(elenco.map(el => el.numoveredit))

    tr = tbl.insertRow();
    td = tr.insertCell(); td.appendChild(document.createTextNode(livello));
    td.classList.add("titlecells");
    td = tr.insertCell(); td.appendChild(document.createTextNode("" + (conf_costo/1000000) + "M" ));
    td = tr.insertCell(); td.appendChild(document.createTextNode(conf_cristalli));
    td = tr.insertCell(); td.appendChild(document.createTextNode(conf_edit*COSTOEDIT + conf_overedit*COSTOEDIT*2));        
    td = tr.insertCell(); td.appendChild(document.createTextNode(conf_edit));
    td = tr.insertCell(); td.appendChild(document.createTextNode(conf_overedit));
  }

  const remove = document.querySelector('.socket-calculator .messaggio')
  if (remove) {remove.remove();}

  domRisultato.appendChild(tbl);
  document.querySelector(".socket-calculator #risultato .titolotabella").style.display = "block";
    let body = document.querySelector('.socket-calculator .body')
    $(body).animate({ scrollTop: body.scrollHeight }, 1000)
  };
  setTimeout(calcolo, 100)
}

