async function editDbInfo(title, dbinfo) {
  dbinfo = JSON.parse(JSON.stringify(dbinfo))
  let re = await Messagebox.ShowMultiInput(title, ["Messaggio", "Data", "Versione"], [dbinfo.msg, formattedDate(), dbinfo.ver])
  if (re.button == 1) {
