      Notification.Warning("Cambio info scartato perche la versione non e' un numero intero maggiore a 0.")
    } else {
      dbinfo.ver = re.results[2]
      dbinfo.data = re.results[1]
      dbinfo.msg = re.results[0]
    }
  }
  return dbinfo;
}


