function importFromFile(callback) {
    if (!callback) return;
    let inp = document.createElement("input");
    inp.type = "file";
    inp.style.visibility = "hidden";

    inp.addEventListener("change", (e) => {
        let file = e.target.files[0];
        if (!file) {
            return;
        }

        let reader = new FileReader();
        reader.onload = (e1) => {
            let text = e1.target.result;
            callback(text)
        };
        reader.readAsText(file);

    });

    document.body.appendChild(inp);
    inp.click();
    document.body.removeChild(inp);
}

async function saveDB(name) {
  name = name || dbState.current
  let dbData = dbState[name].dbData

  let errors = [...getValidation(name).values()].filter(v => v.error).length
  let warnings = [...getValidation(name).values()].filter(v => v.warning).length
  if (errors > 0) {
    let re = await Messagebox.Question("Ci sono " + errors + " errori in " + name + ". Vuoi continuare?")
    if (re.button != 1) return
  }
  if (warnings > 0 && name == databases[0]) {
    let re = await Messagebox.Question("Ci sono " + warnings + " avvertimenti in " + name + ". Vuoi continuare?")
    if (re.button != 1) return
  }

  if (name==databases[0]) {
    dbData.dbinfo = await editDbInfo("Salvataggio di " + name, dbData.dbinfo)
    util.db.dbinfo = dbData.dbinfo
    util.db.ogglistPubl = dbData.data
    util.save(true)
  } else {
    util.db.ogglistPriv = dbData.data
    util.save(false)
  }
  setModified(false, name)
  Notification.Show(name + " salvato")
}

