    const val2 = {};
    val2.ispriv = (String(id2).substring(0,4)=="Priv")
    val2.val = (val2.ispriv) ? Number(id2.substring(4)) : Number(id2);

    if ( (val1.ispriv && val2.ispriv) || (!val1.ispriv && !val2.ispriv) ) {
      //Omogenei
      if (val2.val > val1.val) {
        return {nuovo:id2, vecchio:id1};
      } else {
        return {nuovo:id1, vecchio:id2};
      }
    } else {
      //Disomogenei
      return (val1.ispriv) ? {nuovo:id1, vecchio:id2} : {nuovo:id2, vecchio:id1};
