    /*for (let i = db.dataFixed.length - 1; i >= 0; i--) {
      if ( dbIdentMapID.has(db.dataFixed[i][fPos["ID"]]) ) { 
        db.dataFixed.splice(i,1);
      }
    } */

    //db.data = db.dataFixed; //Tiene la versione fixata
    dbIdent.data = dbIdent.data.concat(db.dataFixed.filter(d => {
      return !dbIdentMapID.has(d[fPos["ID"]])
    }).map(v => JSON.parse(JSON.stringify(v))));
    dbUtil.dbSort(dbIdent);
    dbUtil.searchduplicates(dbIdent, validation, false);
    dbUtil.fixDuplicati(dbIdent, validation, recordDaCancellare);
    databases.forEach(db => setModified(true, db))
    return dbIdentMapID
  },
  fixDuplicati: (dbIdent, validation, mapProvenienzaCopiataSuVecchi) => {
    setModified(true)
    const getmaxdata = (strData1, strData2) => {
      const d1 = (strData1 || "01/01/1900").split("/")
      const d2 = (strData2 || "01/01/1900").split("/")

      const data1 = new Date(d1[2], d1[1] -1, d1[0] );
      const data2 = new Date(d2[2], d2[1] -1, d2[0] );
      const maxdata = new Date(Math.max(data1,data2))
      return ((strData1 == "") && (strData2 == "")) ? "" : formattedDate(maxdata);

      function formattedDate(d = new Date) {
        return [d.getDate(), d.getMonth()+1, d.getFullYear()].map(n => n < 10 ? `0${n}` : `${n}`).join('/');
      }
    }
    //per ogni duplicazione nel set unito decido se posso scartare qualcosa
    for (let itemduplicati of dbIdent.mapDuplicati.values()) {
      for (let idx=1; idx<itemduplicati.length; idx++) {
        //se il vecchio ha provenienza e il nuovo no duplico la provenienza
        if ( (itemduplicati[0][fPos["Provenienza"]] !== "") && (itemduplicati[idx][fPos["Provenienza"]] == "") ) {
          itemduplicati[idx][fPos["Provenienza"]] = itemduplicati[0][fPos["Provenienza"]];
        }
        //se il nuovo ha provenienza e il vecchio no duplico la provenienza
        if ( (itemduplicati[idx][fPos["Provenienza"]] !== "") && (itemduplicati[0][fPos["Provenienza"]] == "") ) {
          itemduplicati[0][fPos["Provenienza"]] = itemduplicati[idx][fPos["Provenienza"]];
          mapProvenienzaCopiataSuVecchi.set(itemduplicati[idx][fPos["ID"]].toString(), itemduplicati[idx])
        }


        //Se ident uguali e provenienza uguale metto ad entrambi la data pi recente
        if ( ( itemduplicati[0][fPos["Identify"]] == itemduplicati[idx][fPos["Identify"]] ) &&
             ( itemduplicati[0][fPos["Provenienza"]] == itemduplicati[idx][fPos["Provenienza"]] ) ) {
          //Cerco tra i 2 la data pi recente
          const datamax = getmaxdata(itemduplicati[0][fPos["Nota"]], itemduplicati[idx][fPos["Nota"]])
          //Metto la data pi recente ad entrambi
          itemduplicati[0][fPos["Nota"]] = datamax;
          itemduplicati[idx][fPos["Nota"]] = datamax;
          mapProvenienzaCopiataSuVecchi.set(itemduplicati[idx][fPos["ID"]].toString(), itemduplicati[idx])
        }

        const orderedid = dbUtil.getmaxid(itemduplicati[0][fPos["ID"]], itemduplicati[idx][fPos["ID"]]);
        if ( itemduplicati[0][fPos["Identify"]] !== itemduplicati[idx][fPos["Identify"]] ) {
          //se ident diversi tengo il pi nuovo ed elimino il pi vecchio
          dbIdent.data = dbIdent.data.filter( el => el[fPos["ID"]] !== orderedid.vecchio );
        } 

        if ( ( itemduplicati[0][fPos["Identify"]]    == itemduplicati[idx][fPos["Identify"]] ) &&
             ( itemduplicati[0][fPos["Provenienza"]] == itemduplicati[idx][fPos["Provenienza"]] ) ) {
          //se ident uguali e provenienza uguale tengo il pi vecchio ed elimino il pi nuovo
          dbIdent.data = dbIdent.data.filter( el => el[fPos["ID"]] !== orderedid.nuovo );
        }
      }
    }
    dbIdent.dataFixed = dbIdent.data;
    return dbUtil.searchduplicates(dbIdent, validation);
  },
  fixId: (dbIdent) => {
    setModified(true)
    //Cerca l'ID da usare (il pi grande)
    let maxID = 0;
    for (let el of dbIdent.data) {
      if ( Number.isInteger(el[fPos["ID"]]) && (Number(el[fPos["ID"]]) > maxID) ) {
        maxID = el[fPos["ID"]];
      }
    }
    let newID = maxID+1;  

    //A tutti quelli non interi (della forma Priv##) assegna il numero
    for (let i in dbIdent.data) {
      if (!Number.isInteger(dbIdent.data[i][fPos["ID"]])) {
        dbIdent.data[i][fPos["ID"]] = newID;
        //if (dbIdent.dataFixed) dbIdent.dataFixed[i][fPos["ID"]] = newID;
        newID++;
      }
    }
  },
  parseRow: (db, item) => {
    const objFixed = dbUtil.parseident(item[fPos["Identify"]]);
    const fixedRow = [];
    (db.fields||[]).sort(f => fPos[f.nome]).forEach(f => {
      fixedRow.push(objFixed[f.nome])
    })
    fixedRow[fPos["Identify"]] = item[fPos["Identify"]]
    fixedRow[fPos["Provenienza"]] = item[fPos["Provenienza"]]
    fixedRow[fPos["ID"]] = item[fPos["ID"]]
    fixedRow[fPos["Nota"]] = item[fPos["Nota"]]

    if (objFixed.parseErrors.length > 0) {
      if (!db.parseErrors) db.parseErrors = new Map()
      db.parseErrors.set(item[fPos["ID"]],objFixed.parseErrors);
    }
    return fixedRow
  },
  dbFix: (db) => {
    setModified(true)
    db.dataFixed = [];
    db.parseErrors= new Map();
    //row._refreshDetails
    for (let item of db.data) {
      let fixedRow = dbUtil.parseRow(db, item)
      db.dataFixed.push(fixedRow);
    }
    gridUtil.refreshRowDetails()
    db.data = db.dataFixed
  },
  dbSort: (db) => {
    setModified(true)
    //dbIdent.data.sort( (a,b) => Number(a[fPos["ID"]]) - Number(b[fPos["ID"]]) ); //Ordina per ID
    db.data.sort( (a,b) => {
      if (
        [a[fPos["Oggetto"]],a[fPos["DescrizioneShort"]],a[fPos["DescrizioneLong"]]].join("|").toLowerCase() >= 
        [b[fPos["Oggetto"]],b[fPos["DescrizioneShort"]],b[fPos["DescrizioneLong"]]].join("|").toLowerCase() ) return 1;
      return -1;
    }); //Ordina per Nome oggetto
  },
  searchduplicates: (db, validation, confrontacolonne = true) => {
    let mapItems = new Map();
    let mapID = new Map();
    let keyduplicate = [];
    db.data.forEach( el => {
      //Costruisco l'elemento univoco ogg|short|long
      let ogg_short_long = [el[fPos["Oggetto"]],el[fPos["DescrizioneShort"]],el[fPos["DescrizioneLong"]]].join("|");

      let rId = el[fPos["ID"]].toString()
      mapID.set(rId, (mapID.get(rId)||0)+1)

      if ((mapID.get(rId)||0)>1) {
        let val = validation.get(rId)||{warning:"",error:""}
        if (!val.error.includes("ID Duplicato")) val.error = val.error + "ID Duplicato\n"
        validation.set(rId, val)
      }

      let mapvalue = mapItems.get(ogg_short_long);
      if (mapvalue === undefined) {
        // se non  in mappa lo aggiungo
        mapItems.set(ogg_short_long,[el]);
      } else {
        // se  gi in mappa aggiungo solo l'item alla stessa chiave
        mapvalue.push(el); 
        keyduplicate.push(ogg_short_long); //e mi tengo traccia delle chiavi che hanno piu' di un elemento per filtare poi
      }
    });

    //tengo solo i mapItems che hanno duplicati
    db.mapDuplicati = new Map();
    for ( let i of mapItems.keys()) {
      let v = mapItems.get(i);
      if (v.length >= 2) {
        db.mapDuplicati.set(i, v);
      } 
    }

    db.duplicati = [];
    db.duplicatiColonnaDiversa = new Map();
    for (let numcolonna=0; numcolonna<db.fields.length; numcolonna++) {
      db.duplicatiColonnaDiversa.set(numcolonna,[]);
    }
    //if (!confrontacolonne) return;

    //Scorro solo quelli con duplicati
    keyduplicate.forEach( (ogg_short_long) => { 
      let identUguali = true;
      let provenienzaUguale = true;
      let colonnaUguale=[]
      for (let numcolonna=0; numcolonna<db.fields.length; numcolonna++) {
        colonnaUguale.push(true);
      }
      //confronto gli ident
      let items = mapItems.get(ogg_short_long);

      for (let idx = 1; idx<items.length; idx++) {
        for (let numcolonna=0; numcolonna<db.fields.length; numcolonna++) {
          if (items[0][numcolonna] !== items[idx][numcolonna]) {
            colonnaUguale[numcolonna] = false;
          }
        }      
      }
      items.map( el => el[fPos["ID"]] ).forEach( id => {
        db.duplicati.push(id)
        let val = validation.get(id.toString())||{warning:"",error:""}
        if (!val.error.includes("Duplicato")) val.error = val.error + "Duplicato\n"
        validation.set(id.toString(), val)
      });
      for (let numcolonna=0; numcolonna<db.fields.length; numcolonna++) {
        if (!colonnaUguale[numcolonna]) {
          items.map( el => el[fPos["ID"]]).forEach( id => { 
            let tmp = db.duplicatiColonnaDiversa.get(numcolonna);
            tmp.push(id);
            let colName = source.dataFields[numcolonna].name
            if (colName != "ID") {
              let vobj = validation.get(id.toString())||{warning:"",error:""}
              vobj.warning += "Colonna cambiata " + numcolonna + " (" + colName + ")\n"
              validation.set(id.toString(), vobj)
            }
          });  
        }
      }  
    });

    return validation
  },
  parseident: (identText) => {
    let obj={};
    obj.parseErrors = [];
    //console.log(identText);

    try {
      obj.Oggetto = identText.match(/^Oggetto '(.+?)' - Tipo: (.+)$/m)[1];
      //se nel nome oggetto trova EDNome per oggetto personalizzato lo toglie
      obj.Oggetto = obj.Oggetto.replace(/\b(\s?ED\S+)/,'').trim();

      obj.Tipo = identText.match(/^Oggetto '(.+?)' - Tipo: (.+)$/m)[2];
    }
    catch(err) {
      obj.parseErrors.push("Parse error in Oggetto+tipo\n" + err)
      obj.Oggetto = "";
      obj.Tipo = "";
    }

    try {
      //Nelle DescrizioneShort e Long sostituisco eventualmente gli a-capo con uno spazio
      //Negli ident ricevuto da webclient non viene ricevuto il /t sugli a-capo
      //  quindi non c'e' bisogno di toglierlo
      obj.DescrizioneShort = identText.match(/^Descrizione (?:corta|short): '(.+?)'$/ms)[1];
    }
    catch(err) {
      obj.parseErrors.push("Parse error in DescrizioneShort\n" + err)
      obj.DescrizioneShort = "";
    }

    try {
      obj.DescrizioneLong = identText.match(/^Descrizione (?:lunga|long): '(.*?)'$/ms)[1];
    }
    catch(err) {
      obj.parseErrors.push("Parse error in DescrizioneLong\n" + err)
      obj.DescrizioneLong = "";
    }

    try {
      obj.PosizioneIndossabile="";
      if (identText.search(/^Posizione indossabile: (.+)$/m)>-1) {
        let posizioni = identText.match(/^Posizione indossabile: (.+)$/gm);
        for (let posizione of posizioni) {
          if (obj.PosizioneIndossabile.length>0) {obj.PosizioneIndossabile += ", ";}
          obj.PosizioneIndossabile += posizione.match(/^Posizione indossabile: (.+)$/)[1]
        }
        obj.PosizioneIndossabile = obj.PosizioneIndossabile.trim();
      }
    }
    catch(err) {
      obj.parseErrors.push("Parse error in PosizioneIndossabile\n" + err)
      obj.PosizioneIndossabile = "";
    }

    try {
      if (identText.search(/^L'oggetto non ha particolari caratteristiche\.$/m)>-1) {
          obj.Proprieta = "";
      } else if (identText.search(/^L'oggetto e': (.+)$/m)>-1) {
          obj.Proprieta = identText.match(/^L'oggetto e': (.+)$/m)[1].trim();
      } else {
          obj.Proprieta = "";
      }
    }
    catch(err) {
      obj.parseErrors.push("Parse error in Proprieta\n" + err)
      obj.Proprieta = "";
    }

    try {
      obj.Peso =   Number(identText.match(/^Peso: (\d+), Valore: (\d+), Costo di (?:affitto|Rent): (\d+)/m)[1]);
    }
    catch(err) {
      obj.parseErrors.push("Parse error in Peso\n" + err)
      obj.Peso = 0;
    }

    try {
      obj.Valore = Number(identText.match(/^Peso: (\d+), Valore: (\d+), Costo di (?:affitto|Rent): (\d+)/m)[2]);
    }
    catch(err) {
      obj.parseErrors.push("Parse error in Valore\n" + err)
      obj.Valore = 0;
    }

    try {
      obj.Rent = Number(identText.match(/^Peso: (\d+), Valore: (\d+), Costo di (?:affitto|Rent): (\d+)/m)[3]);
    }
    catch(err) {
      obj.parseErrors.push("Parse error in Rent\n" + err)
      obj.Rent = 0;
    }

    try {
      obj.Rare = ( (identText.search(/^Peso: (\d+), Valore: (\d+), Costo di (?:affitto|Rent): (\d+) \[(?:RARO|RARE)\]/m)>-1) || (obj.Proprieta.search("RARE")>-1) );
    }
    catch(err) {
      obj.parseErrors.push("Parse error in Rare\n" + err)
      obj.Rare = false;
    }
    try {
      obj.NoRent = identText.search(/^Peso: (\d+), Valore: (\d+), Costo di (?:affitto|Rent): (\d+) (?:\[NON-RENTABILE\]|\[NORENT\]|NORENT)/m)>-1
    }
    catch(err) {
      obj.parseErrors.push("Parse error in NoRent\n" + err)
      obj.NoRent = false;
    }

    obj.Identify = identText;
    obj.Provenienza = "";
    obj.Nota = "";

    try {
      //Sezione Affects
      for (let i=1; i<=5; i++) {
        obj["Affect" + i + "Tipo"] = "";
        obj["Affect" + i + "Valore"] = "";
      }

      obj.Danno = 0;
      obj.SP = 0;
      obj.HP = 0;
      obj.Mana = 0;
      let abilspec = identText.search(/^Abilita' speciali:$/m);
      if (abilspec > -1) {
        let affects = identText.substring(abilspec).match(/^\s*(Affects:|Aumenta|Diminuisce|Riduce|Abilita|Conferisce) (.+)$/gm);
        for (let idxaffect in affects) {
          let idx = Number(idxaffect)+1;
          let affectparts = affects[idxaffect].match(/^\s*(Affects:|Aumenta|Diminuisce|Riduce|Abilita|Conferisce) (.+) (by|di) (.+)$/)
          if (!affectparts && affects[idxaffect].match(/Conferisce (PERK|BENEFICIO)/)) {
            affectparts = affects[idxaffect].match(/^\s*(Affects:|Aumenta|Diminuisce|Riduce|Abilita|Conferisce) ([A-Z -_]+) ?(by|di)? (.+)$/);
          }
          //let affectparts = affects[idxaffect].match(/^\s*(Affects:|Aumenta|Diminuisce|Riduce|Abilita|Conferisce) (.+) (by|di) (.+)$/);
          //let affectparts = affects[idxaffect].match(/^\s*(Affects:|Aumenta|Diminuisce|Riduce|Abilita|Conferisce) ([A-Z -_]+) ?(by|di)? (.+)$/);
          obj["Affect" + idx + "Tipo"] = (String(affectparts[2]).trim());
          if (affectparts[1].search(/Diminuisce|Riduce/)>-1) {
            obj["Affect" + idx + "Valore"] = "-" + String(affectparts[4]).trim();
          } else {
            obj["Affect" + idx + "Valore"] = (String(affectparts[4]).trim());
          }
          if (obj["Affect" + idx + "Tipo"] == "DAMROLL")     {obj.Danno += Number(obj["Affect" + idx + "Valore"]);}
          if (obj["Affect" + idx + "Tipo"] == "HIT&DAM")     {obj.Danno += Number(obj["Affect" + idx + "Valore"]);}
          if (obj["Affect" + idx + "Tipo"] == "HIT-N-DAM")   {obj.Danno += Number(obj["Affect" + idx + "Valore"]);}
          if (obj["Affect" + idx + "Tipo"] == "MANA")        {obj.Mana += Number(obj["Affect" + idx + "Valore"]);}
          if (obj["Affect" + idx + "Tipo"] == "HIT")         {obj.HP += Number(obj["Affect" + idx + "Valore"]);}
          if (obj["Affect" + idx + "Tipo"] == "SPELL-POWER") {obj.SP += Number(obj["Affect" + idx + "Valore"]);}
          if (obj["Affect" + idx + "Tipo"] == "SPELLFAIL")   {obj.SF += Number(obj["Affect" + idx + "Valore"]);}
        }
        let affCol = ""
        for (let i=1; i<=5; i++) {
          if (obj["Affect" + i + "Tipo"]) {
            let affline = obj["Affect" + i + "Tipo"] + " : " + obj["Affect" + i + "Valore"];
            affCol += affline + "\n"
          }
        }
        obj.Affects = affCol
      }
    }
    catch(err) {
      obj.parseErrors.push("Parse error in Affects\n" + err)
      for (let i=1; i<=5; i++) {
        obj["Affect" + i + "Tipo"] = "";
        obj["Affect" + i + "Valore"] = "";
      }
      console.log(err)
    }

    try {
      let danno = identText.match(/^Danno: .+ (\d+)d(\d+), in condizioni ottimali d(\d+)$/m);
      if (danno != null) {
        obj.DannoArma = Number(danno[1] * danno[3]);
      } else {
        obj.DannoArma = 0;
      }

    }
    catch(err) {
      obj.parseErrors.push("Parse error in DannoArma\n" + err)
      obj.DannoArma = 0;
    }
    return obj;
  },
  validateDB: (db) => {
    const validation = new Map();
    db.parseErrors = new Map()
    db.duplicati = []
    dbUtil.searchduplicates(db, validation, true)
    let index = 0
    for (let v of db.data) {
      let id = (v[parseInt(fPos["ID"])]).toString()
      if (id.includes("_")) {
        let valObj = null
        if (validation.get(id)) {
          valObj = validation.get(id)
        } else {
          valObj = { error: "", warning: ""}
          validation.set(id, valObj)
        }
        if (dbState.current != databases[0]) {
          valObj.warning = (valObj.warning||"") + "Oggetto con ID non valido\n"
        } else {
          valObj.error = (valObj.error||"") + "Oggetto con ID non valido\n"
        }
      }
      if (id.startsWith("Priv") && dbState.current == databases[0]) {
        let valObj = null
        if (validation.get(id)) {
          valObj = validation.get(id)
        } else {
          valObj = { error: "", warning: ""}
          validation.set(id, valObj)
        }
        valObj.error = (valObj.error||"") + "Oggetto Privato in Database pubblico\n"
      }
      if ((v[parseInt(fPos["Provenienza"])]).toString().trim()=="" && dbState.current != databases[0]) {
        let valObj = null
        if (validation.get(id)) {
          valObj = validation.get(id)
        } else {
          valObj = { error:"", warning: ""}
          validation.set(id, valObj)
        }
        valObj.warning = (valObj.warning||"") + "Oggetto senza provenienza in Database privato\n"
      }
      if (dbState.current == databases[0] && dbUtil.isItemPrivato(v)) {
        let valObj = null
        if (validation.get(id)) {
          valObj = validation.get(id)
        } else {
          valObj = { error:"", warning: ""}
          validation.set(id, valObj)
        }
        valObj.warning = (valObj.warning||"") + "Oggetto Personale in Database pubblico\n"
      }
      let o = dbUtil.parseident(v[parseInt(fPos["Identify"])])

      if (o.parseErrors.length) {
        if (!db.parseErrors) db.parseErrors = new Map()
        db.parseErrors.set(v[parseInt(fPos["ID"])], o.parseErrors)

        let valObj = null
        if (validation.get(id)) {
          valObj = validation.get(id)
        } else {
          valObj = { warning: "", error: ""}
          validation.set(id, valObj)
        }
        o.parseErrors.forEach(ve => valObj.error = (valObj.error||"") + ve + "\n") 
      }
      if (!v[parseInt(fPos["Tipo"])]) {
        let valObj = null
        if (validation.get(id)) {
          valObj = validation.get(id)
        } else {
          valObj = { error: "", warning: ""}
          validation.set(id, valObj)
        }
        valObj.warning = (valObj.warning||"") + "Oggetto non parsato\n" 
      }
      function evidenziaerrore(colonna) {
        if (v[colonna] != db.dataFixed[index][colonna] ) {
          if (String(v[colonna]).trim() == String(db.dataFixed[index][colonna]).trim()) {
            let val = validation.get(id)||{ warning: "", error: ""};
            val.warning = (val.warning||"") + "Spazi diversi su colonna "+colonna
            validation.set(id, val)
          } else {
            let val = validation.get(id)||{ warning: "", error: ""};
            val.error = (val.error||"") + "Parsato male colonna "+colonna
            validation.set(id, val)
          } 
        }
      }
      for (let i = 0; i < fPos.length; i++) {
        evidenziaerrore(i)
      }
      index++;
    }
    return validation
  }
}

