  deleteSelected: async () => {
    let rows = gridUtil.getSelectedRows().map(r => r.ID.toString())
    let idPos = fPos["ID"]
    if (rows.length < 1) {
      Notification.Show("Nessun oggetto selezionato")
      return
    }
    let re = await Messagebox.Question("Sei sicuro di voler cancellare " + rows.length + " oggetti da " + dbState.current + "?")
    if (re.button != 1) {
      return
    }
    getDBData().data = getDBData().data.filter( el => !rows.includes(String(el[idPos])));
    if (getDBData().dataFixed) getDBData().dataFixed = getDBData().dataFixed.filter( el => !rows.includes(String(el[idPos])));
    gridUtil.setSource(dbUtil.loadDatabase(dbState.current))
    Notification.Show(rows.length + " oggetti cancellati")
  },
  parseSelected: () => {
    let rows = gridUtil.getSelectedRows()
    if (rows.length < 1) {
      Notification.Show("Nessun oggetto selezionato")
      return
    }
    for (let row of rows) {
      let gr = gridUtil.updateRowField(row.ID, "Identify", row.Identify)
    }
    gridUtil.refreshRowDetails()
    Notification.Show(rows.length + " oggetti parsati")
  },
  duplicateSelected: () => {
    let rows = gridUtil.getSelectedRows()
    if (rows.length < 1) {
      Notification.Show("Nessun oggetto selezionato")
      return
    }
    let dbIdent = getDBData()
    let idPos = fPos["ID"]
    for (let row of rows) {
      let copy = JSON.parse(JSON.stringify(rowsByID.get(row.ID.toString())));
      copy[idPos] = String(copy[idPos]) + "_c";
      dbIdent.data.push(copy);
    }
    dbUtil.dbSort(dbIdent);
    gridUtil.setSource(dbUtil.loadDatabase(dbState.current))
    Notification.Show(rows.length + " oggetti clonati")
  },
  fondiPrivati: async () => {
    if (dbState.current == databases[0]) {
      Notification.Warning("Non puoi farlo sulla schermata del DB pubblico")
      return
    }
    let errors = [...getValidation().values()].filter(v => v.error).length
    let warnings = [...getValidation().values()].filter(v => v.warning).length
    if (errors > 0) {
      Notification.Warning("Ci sono " + errors + " errori. Devi prima risolverli.")
      return
    }
    if (warnings > 0) {
      let re = await Messagebox.Question("Ci sono " + warnings + " avvertimenti. Vuoi continuare?")
      if (re.button != 1) return
    }
    let validation = new Map()
    let recordDaCancellare = new Map()
    let oldDbIdentMapID = dbUtil.mergeAndFix(dbPrivato, dbPubblico, validation, recordDaCancellare)
    let dbIdentMapID = new Map();
    for (let item of dbPubblico.data) {
      dbIdentMapID.set(item[fPos["ID"]],item);
    }
    Notification.Show("Merge eseguito. Controlla di non avere ID privati nel pubblico!")
    gridUtil.setSource(dbUtil.loadDatabase(dbState.current))
    let ref = await Messagebox.Question("Vuoi cancellare dal privato gli oggetti mergiati?")
    if (ref.button == 1) {
      for (let i = dbPrivato.data.length - 1; i >= 0; i--) {
        if ( dbIdentMapID.has(dbPrivato.data[i][fPos["ID"]]) ||
            recordDaCancellare.has(dbPrivato.data[i][fPos["ID"]].toString())
           ) { 
          dbPrivato.data.splice(i,1);
        }
      }
    }
    gridUtil.setSource(dbUtil.loadDatabase(databases[0]))
    errors = [...getValidation().values()].filter(v => v.error).length
    if (errors > 0) {
      let re = await Messagebox.Question("Attenzione il risultante DB pubblico ha " + errors + " errori.\nVuoi visualizzarli?")
      if (re.button == 1) {
        actions.mostraErroriEAvvertimenti()
      }
    }
  },
  cleanPrivati: () => {
    if (dbState.current == databases[0]) {
      Notification.Warning("Non puoi farlo sulla schermata del DB pubblico")
      return
    }
    let map = new Map();
    for (let el of dbPubblico.data) {
      let key = [el[fPos["Oggetto"]],el[fPos["DescrizioneShort"]],el[fPos["DescrizioneLong"]]].join("|");
      map.set(key,el);
    }
    let cnt = 0
    for (let i = dbPrivato.data.length - 1; i >= 0; i--) {
      let el = dbPrivato.data[i]
      let key = [el[fPos["Oggetto"]],el[fPos["DescrizioneShort"]],el[fPos["DescrizioneLong"]]].join("|");
      if ( map.has(key) && map.get(key)[fPos["Provenienza"]] && !el[fPos["Provenienza"]]) {
        cnt++;
        dbPrivato.data.splice(i,1);
      }
    }
    if (cnt>0) {
      setModified(true)
    }
    Notification.Show(cnt + " oggetti privati senza provenienza presenti nel pubblico sono stati cancellati")
    gridUtil.setSource(dbUtil.loadDatabase(dbState.current))
  },
  caricaJSON: () => {
    importFromFile(dbState.current==databases[0]?importPublico:importPrivato)
  },
  saveAllDB: () => {
    databases.forEach(db => saveDB(db))
  },
  saveCurrentDB: () => {
    saveDB()
  },
  esportaIdentInJSON: async () => {
    if (getModified()) {
      Notification.Show("Stai esportando dati non salvati da memoria. Ricordati di salvare se lo volevi.")
    }
    let db = getDBData()
    let dbName = dbState.current
    let obj = {
      data: db.data,
      fields: dbPubblico.fields,
      dbinfo: db.dbinfo
    }
    if (dbName == databases[0]) {
      obj.dbinfo = db.dbinfo;
      obj.dbinfo = await editDbInfo("Versione export di DB pubblico", obj.dbinfo)
      downloadJsonToFile({ dbinfo: obj.dbinfo }, "identDbPublicVersion.json")
    } else {
      obj.dbinfo.data = formattedDate()
      obj.dbinfo.msg = "Client export"
    }
    let filename = "Export "+ dbState.current + " v" + obj.dbinfo.ver + " " + formattedDate(new Date(), '') + ".json"
    downloadJsonToFile(obj, filename)
  },
}

function downloadJsonToFile(json, filename) {
    let jsonstr = JSON.stringify(json, null, 2);
    downloadString(jsonstr, filename);
}

function downloadString(jsonstr, filename) {
    let blob = new Blob([jsonstr], { type: "octet/stream" });
    let url = window.URL.createObjectURL(blob);

    let link = document.createElement("a");
    link.setAttribute("href", url);
    link.setAttribute("download", filename);
    link.style.visibility = "hidden";

    document.body.appendChild(link);
    link.click();
    window.URL.revokeObjectURL(url);
    document.body.removeChild(link);
}

