    applyIde.click(ev => {
      let newIde = notescontainer[0].textContent
      let gridRow = gridUtil.updateRowField(row.ID.toString(), 'Identify', newIde)
      updateRowDetails(gridRow)
      if (gridRow.ParseError) {
        notescontainer.css({ "color": "red"})
      } else  {
        notescontainer.css({ "color": ""})
      }
      $(applyIde, $(leftcolumn)).css({"display":"none"})
    })
    $(".saveprovenienza", $(leftcolumn)).click(ev => {
      let newProv = ($(".inputprovenienza", $(leftcolumn)).val())
      let gridRow = gridUtil.updateRowField(row.ID.toString(), 'Provenienza', newProv)
      let rowIndex = source.localData.indexOf(gridRow)
      $("#identDataTable", $(win)).jqxDataTable('updateRow', rowIndex, gridRow)
      updateRowDetails(gridRow)
      $(".saveprovenienza", $(leftcolumn)).css({"display":"none"})
    })
    $(".inputprovenienza", $(leftcolumn)).on("input", () => {
      if ($(".inputprovenienza", $(leftcolumn)).val() != row.Provenienza) {
        $(".saveprovenienza", $(leftcolumn)).css({
          "display": "inline-block"
        })
      }
    })
    $(notescontainer).on("input", () => {
      if (notescontainer[0].textContent != row.Identify) {
        $(applyIde).css({
          "display": "inline-block"
        })
      }
    })
    $(tabsdiv).jqxTabs({
      width: 700,
      height: 260
    });
  }
}


