const nvolte = match[1];
if (nvolte>50) {
  print("Numero troppo grande di ripetizioni richieste");
  return;
}

const comando = match[2];
if (!comando || (nvolte==0)) {
  print("USO: NNx comando1|comando2|...|comando")
  print("         dove NN  un numero da 1 a 50")
  print("         eventuali # verranno convertiti nel numero della ripetizione")
  return;
}

let result;
for (let i=1; i<=nvolte; i++) {
	result = comando.replace("#", i)
  result = result.replace("|", ";")
	send(result)
}
