function isMob(str) {
  let mob = false;
  if (str.toLowerCase().indexOf("strillone")!=-1) mob = true;
  if (str.toLowerCase().indexOf("dhethor")!=-1) mob = true;
  if (str.toLowerCase().indexOf("guardia ")!=-1) mob = true;
  if (str.toLowerCase().indexOf("matteo")!=-1) mob = true;
  if (str.toLowerCase().indexOf("elminster")!=-1) mob = true;
  if (str.toLowerCase().indexOf("eowarion")!=-1) mob = true;
  if (str.toLowerCase().indexOf("uno ")!=-1) mob = true;
  if (str.toLowerCase().indexOf("una ")!=-1) mob = true;
  if (str.toLowerCase().indexOf("biff")!=-1) mob = true;
  if (str.toLowerCase().indexOf(",")!=-1) mob = true;
  if (str.toLowerCase().startsWith("il ")) mob = true;
  if (str.toLowerCase().startsWith("l'")) mob = true;
  if (str.toLowerCase().startsWith("la ")) mob = true;
  if (str.toLowerCase().startsWith("lo ")) mob = true;
  if (str.toLowerCase().startsWith("un ")) mob = true;
  if (str.toLowerCase().startsWith("una ")) mob = true;
  if (str.toLowerCase().startsWith("uno ")) mob = true;
  return mob;
}
