  this.grandezzaGruppo++;
  if (this.grandezzaGruppo == 1) {
    capogruppoInRoom = grp[nome].inroom;
  }
  const numero = cnt.toString().padStart(2)+".";
  const pg = this.colorizeHp(grp[nome].hp, nome.substr(0, 10).padStart(10));
  const hp = this.colorizeHp(grp[nome].hp, grp[nome].hp.toString().padStart(3));
  const mana = this.colorizeMana(grp[nome].mn, grp[nome].mn.toString().padStart(3));
  const mov = this.colorizeMov(grp[nome].mv,grp[nome].mv.toString().padStart(3));
  const sanc = this.colorizeBool(grp[nome].sanc||(this.TSSettore!="Chiuso" && grp[nome].verde), ((grp[nome].sanc>1||(this.TSSettore!="Chiuso" && grp[nome].verde>1)) ? "+" : ((grp[nome].sanc==1||(this.TSSettore!="Chiuso" && grp[nome].verde==1))?"!":"-")).padStart(3));
  const detect = this.colorizeBool(grp[nome].do, (grp[nome].do ? "+" : (grp[nome].do==1?"!":"-")).padStart(3));
  const qui = this.colorizeBool(grp[nome].inroom, (grp[nome].inroom ? "+" : "-").padStart(3));
  const hide = (grp[nome].hide ? "+" : "-").padStart(3);
  const linea = color(`${numero} ${pg}: ${hp} ${mana} ${mov}  ${sanc} ${detect} ${qui}\n`, "lightgray");
  cnt++;
  grStr += linea;
}
