this.limitaLista = (lista) => {
  if (!lista) return;
  if (lista.size) {
    const surplus = lista.size - 600;
    for (let i = 0; i < surplus; i++) {
      lista.delete(lista.keys().next)
    }
  } else if (lista.length) {
    const surplus = lista.length - 600;
    for (let i = 0; i < surplus; i++) {
      lista.splice(0, 1)
    }
  }
}
