const printPageWrap = (pageFunc, window, title, pageIndex, pageSize) => {
  cls(window)
  if (this.debug) print(`Mostro pagina ${pageIndex} di ` + window)
  print(color(title,"lighgreen")+ "\n",window)

  const numPages = Math.ceil(pageFunc(pageIndex, pageSize) / pageSize)
  const prevPage = Math.max(pageIndex - 1, 0)
  const nextPage = Math.min(pageIndex + 1, numPages - 1)

  const footer = (pageIndex > 0 ? link("<== ", () => printPageWrap(pageFunc, window, title, prevPage, pageSize)) : "<== ") +
        " Pag." + (pageIndex+1)  + "/" + numPages + " " +
        (pageIndex < numPages - 1 ? link(" ==>", () => printPageWrap(pageFunc, window, title, nextPage, pageSize)) : " ==>")

  print("\n" + footer,window);
}

this.pageContent = async (window, pageFunc, title = "", pageIndex = 0, pageSize = 20, w, h, x, y) => {

  let wnd = getWindow(window)
  if (wnd?.data?.collapsed) {
    // se c'e' ed era colassata serve tweakare e ricreare con i wnd.data
    deleteWindow(window,true)
    wnd.data.collapsed = false;
    wnd.initialized = true; // prevent docking
    wnd = createWindow(window, wnd.data)
  }

  if (!wnd) {
    // se non esiste questi sono i default
    const data = {
      w: w || 400,
      h: h || 450,
      x: x || 300,
      y: y || 300,
      visible: false,
      collapsed: false,
      docked:false
    }
  	wnd = createWindow(window, data)
    wnd.initialized = true; // prevent docking
  }

  let tmpdata = null;

  if (wnd) {
    // se esiste ed era chiusa serve tweakarla
    wnd.initialized = true;    // no docking
    tmpdata = wnd.data;

    if (tmpdata) { // togli la roba fatta dall user
      if (w) tmpdata.w = w;
      if (h) tmpdata.h = h;
      if (x) tmpdata.x = x;
      if (y) tmpdata.y = y;
    	tmpdata.visible = true;
      	tmpdata.collapsed = false;
      	tmpdata.docked = false;
    }
    // la cancello cosi la ricrea con i tmpdata
    await outputManager.getWindowManager().destroyWindow(window,true)
  }
  wnd = createWindow(window, tmpdata)
  if (wnd) wnd.initialized = true; // non si sa mai, prevent docking

  printPageWrap(pageFunc, window, title, pageIndex, pageSize)

}

