let spells = this.TSSpells;
this.spells="";
for (let i=0;i<spells.length;i++) {
  switch (spells[i]) {
    case 'F':
    case 'f':
      {
        switch (spells[i+1]) {
          case '2':
            this.spells += color(spells[i],"lightblue", null, true)
            i++;
            break;
          case '3':
            this.spells += color(spells[i],"blue", null, true)
            i++;
            break;
          case '4':
            this.spells += color(spells[i],"yellowgreen", null, true)
            i++;
            break;
          case '3':
            this.spells += color(spells[i],"yellow", null, true)
            i++;
            break;
          default:
            this.spells += color(spells[i],"red", null, true)
        }
      }
      break;
    case 'S':
    case 's':
      this.spells += color(spells[i],"white", null, true)
      break;
    case 'N':
    case 'n':
