util.parseident = function(identText) {
  let obj={};

  try {
    obj.Oggetto = identText.match(/^Oggetto '(.+?)' - Tipo: (.+)$/m)[1];
    obj.Tipo = identText.match(/^Oggetto '(.+?)' - Tipo: (.+)$/m)[2];
  }
  catch(err) {
    obj.Oggetto = "";
    obj.Tipo = "";
  }

  try {
    obj.DescrizioneShort = identText.match(/^Descrizione (?:corta|short): '(.+?)'$/ms)[1].replace("\n"," ");
  }
  catch(err) {
    obj.DescrizioneShort = "";
  }

  try {
    obj.DescrizioneLong = identText.match(/^Descrizione (?:lunga|long): '(.*?)'$/ms)[1].replace("\n"," ");
  }
  catch(err) {
    obj.DescrizioneLong = "";
  }

  obj.Identify = identText;
  obj.Provenienza = ""; 
  return obj;
}

